function searchcall () {
    var items = JSON.parse(localStorage.getItem("search"));
        var div = document.getElementById("searchchall");
    var html = "";
    for (var i = 0; i < items.length; i++) {
        html += `<div style = "display: flex;padding:10px;align-items: center;  border-bottom: solid 0.5px #f0f0f0; margin-top:10px;">
                        <div >
                            <img src="${items[i].img}"/>
                        </div>
                        <div style= "margin-left: 20px">
                            <span  style = "font-size: 15px; font-weight: bold;">${items[i].name} </span>
                            <br/>
                            <span style = "font-size: 11px;">${items[i].desc}</span>
                        </div>
                    </div>`
    }
    div.innerHTML = html;
}
function findchall() {
    var searchres = [];

    document.getElementById("searchchall");
    document.getElementById("recmhide").style.display="none";
    document.getElementById("srcshow").style.display="block";
    var txt = document.getElementById("searchtxt").value;
    var local = JSON.parse (localStorage.getItem("recm"));
    for (var i = 0; i < local.length; i++) {
        var text1 = local[i].name.toLowerCase().includes(txt.toLowerCase());
        var text2 = local[i].desc.toLowerCase().includes(txt.toLowerCase());
        if(text1 || text2){
            searchres.push(local[i])
        }
    }
    
    localStorage.setItem("search",JSON.stringify(searchres));
    searchcall();
    
}

